    //Função para Cadastrar um novo cliente/serviço
    public void Cadastrar() {

        //Instanciando um objeto c do tipo Cliente.
        Cliente c = new Cliente();

        //Condição de validação de campos.
        if (txtnome.getText().equals("") || txtfieldcontato.getText().equals("") || txtpreco.getText().equals("") || txtfielddia.getText().equals("") || txtfieldhora.getText().equals("") || txtid.getText().equals("")) {

            JOptionPane.showMessageDialog(null, "Erro ao Cadastrar, há campos vazios!", "Erro!", JOptionPane.ERROR_MESSAGE);

        } else {

            //Passando o texto digitando nos campos preco e desconto para float.
            float preco = Float.parseFloat(txtpreco.getText());
            float desconto = Float.parseFloat(txtdesconto.getText());

            //Setando os atributos escritos, cadastrando e inserindo os mesmos no arquivo.
            c.setNome(txtnome.getText());
            c.setContato(txtfieldcontato.getText());
            c.setEndereco(txtendereco.getText());
            c.setServico(comboservico.getSelectedItem().toString());
            c.setPreco(preco);
            c.setDesconto(desconto);
            c.setDia(txtfielddia.getText());
            c.setHora(txtfieldhora.getText());
            c.setId(txtid.getText());

            float valorFinal = (preco - desconto);

            c.setValFinal(valorFinal);

            //Chamando metodo de Cadastrar Serviço através do objeto da classe Cliente instanciado.
            c.cadastrarServico(c);
            JOptionPane.showMessageDialog(null, "Cadastro realizado com sucesso!", "Sucesso!", JOptionPane.INFORMATION_MESSAGE);

        }
    }

    //Função para limpar os Campos de texto.
    public void limparCampos() {
        txtnome.setText("");
        txtfieldcontato.setText("");
        txtendereco.setText("");
        comboservico.setSelectedIndex(0); //Setando o índice 0 ao limpar campos.
        txtpreco.setText("");
        txtdesconto.setText("");
        txtfielddia.setText("");
        txtfieldhora.setText("");
        txtid.setText("");
    }

