    private void txtnomeKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtnomeKeyTyped
        //Utilizando a classe MyDocumentFilter para filtrar os caracteres digitados (typed),
        //permitindo apenas caracteres do tipo "String" (letras).
        //Obs: A classe MyDocumentFilter extende a classe abstrata DocumentFilter 
        ((AbstractDocument) txtnome.getDocument()).setDocumentFilter(new DocumentFilter() {
            Pattern regEx = Pattern.compile("^[a-zA-Z0_ ]*$");

            @Override
            public void replace(DocumentFilter.FilterBypass fb, int offset, int length, String text, AttributeSet attrs) throws BadLocationException {
                Matcher matcher = regEx.matcher(text);
                if (!matcher.matches()) {
                    return;
                }
                super.replace(fb, offset, length, text, attrs);
            }
        });
    }//GEN-LAST:event_txtnomeKeyTyped
    private void txtidKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtidKeyTyped
        //Novamente utilizando a classe MyDocumentFilter para filtrar os caracteres digitados (typed),
        //permitindo apenas números inteiros.
        ((AbstractDocument) txtid.getDocument()).setDocumentFilter(new DocumentFilter() {
            Pattern regEx = Pattern.compile("\\d*");

            @Override
            public void replace(FilterBypass fb, int offset, int length, String text, AttributeSet attrs) throws BadLocationException {
                Matcher matcher = regEx.matcher(text);
                if (!matcher.matches()) {
                    return;
                }
                super.replace(fb, offset, length, text, attrs);
            }
        });
    }//GEN-LAST:event_txtidKeyTyped
    private void txtprecoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtprecoKeyTyped
        // TODO add your handling code here:
        //Novamente utilizando a classe MyDocumentFilter para filtrar os caracteres digitados (typed),
        //permitindo apenas números inteiros e pontos (.).
        ((AbstractDocument) txtpreco.getDocument()).setDocumentFilter(new DocumentFilter() {
            Pattern regEx = Pattern.compile("\\d*\\.?\\d*");

            @Override
            public void replace(DocumentFilter.FilterBypass fb, int offset, int length, String text, AttributeSet attrs) throws BadLocationException {
                Matcher matcher = regEx.matcher(text);
                if (!matcher.matches()) {
                    return;
                }
                super.replace(fb, offset, length, text, attrs);
            }
        });
    }//GEN-LAST:event_txtprecoKeyTyped
